mapper.update:
update register
set biz_code = #{bizCode,jdbcType=INTEGER},
  biz_name = #{bizName,jdbcType=VARCHAR},
  edges = #{edges,jdbcType=VARCHAR},
  `valid` = #{valid,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, biz_code, biz_name, edges, `valid`
from register
where id = #{id,jdbcType=BIGINT}
